<?phpfunction create_appointment() {    $root = INK_ADMIN;    $apturl = admin_url('admin.php?page=createappoitment', 'http');    global $wpdb, $apt_service, $apt_timeslot, $apt_dateslot;//delete area    /* --------Apt Service delete row ------------ */    if (isset($_GET['delete-service'])) {        $id = $_GET['delete-service'];        $wpdb->query($wpdb->prepare("DELETE FROM $apt_service WHERE service_id = %d", $id));        $wpdb->query($wpdb->prepare("DELETE FROM $apt_dateslot WHERE service_id = %d", $id));// $time_count = $wpdb->get_var( "SELECT COUNT(service_id) FROM $apt_timeslot WHERE service_id='$id'" );        $wpdb->query($wpdb->prepare("DELETE FROM $apt_timeslot WHERE service_id = %d", $id));    }// time slot delete    if (isset($_GET['delete-timeslot'])) {        $id = $_GET['delete-timeslot'];        $wpdb->query($wpdb->prepare("DELETE FROM $apt_timeslot WHERE timeslot_id = %d", $id));    }// date slot delete    if (isset($_GET['reset-dateslot'])) {        $id = $_GET['reset-dateslot'];        $wpdb->query($wpdb->prepare("DELETE FROM $apt_dateslot WHERE service_id = %d", $id));    }//update area    /* --------Apt Service update row ------------ */    if (isset($_POST['update-service'])) {        $wpdb->update($apt_service, array('service_name' => $_POST['upname'], 'service_price' => $_POST['upprice']), array('service_id' => $_POST['srid']), array('%s', '%s'), array('%d'));    }    if ($_GET['page'] == 'createappoitment') {        if (isset($_POST['sradd'])) {            $as = new AptService();            $as->insert_service($_POST['srname'], $_POST['srprice'], $_POST['srrand']);        }        ?>			<style>			.ink_appointment_pro_branding{              height: 340px;              width: 901px;              border: 2px solid #c7ffd0;              background: url("<?php echo plugins_url('/images/ink-appointment.png', __FILE__);   ?>") no-repeat;			}			.ink_appointment_learn{              margin-top: 270px;              float: right;              cursor: pointer;			}			</style>        <div class="aptservice">		<div class="ink_appointment_pro_branding"><a target="_blank" href="http://www.inkthemes.com/plugin/wordpress-appointment-plugin/"> <img class="ink_appointment_learn" src="<?php  echo plugins_url('/images/ink-appointment-learnmore.png', __FILE__); ?>"> </a></div>            <h1 class="aptheading"></h1>            <form action="<?php echo $apturl . '&service=service'; ?>" method="post">                <table id="ap_service" class="wp-list-table widefat fixed pages" >                    <thead >                        <tr>                            <th  scope="col" class="ap_srname"> <label>Enter Service Name:</label> </th>                            <th  scope="col"> <input type="text" name="srname" id="srname" placeholder=" Name" required /></th>                            <th  scope="col" id="price"> <label>Price:</label></th>                            <th  scope="col"> <input type="text" name="srprice" id="srprice" title="Enter the Price for the service in numeric value: Example: 10, 15, 20" required /></th>                            <th  scope="col"><input type="submit" name="sradd" id="sradd" value="Add New Service" /></th>                    <input type="hidden" name="srrand" id="srrand" value="<?php echo rand(); ?>" />                    </tr>                    </thead>                </table>            </form>            <div class="show_service">                <table id="show_service" class="wp-list-table widefat fixed pages">                    <thead>                        <tr>                            <th  scope="col" class="srno">Sr. No.</th>                            <th  scope="col" class="srname">Service Name</th>                            <th  scope="col" class="srprice">Service Price</th>                            <th  scope="col" class="sradddate">Add Date Slot</th>                            <th  scope="col" class="srnaddtime">Add Time Slot</th>                            <th  scope="col" class="sraction"><label>Action</label></th>                        </tr>                    </thead>                    <tbody> <?php        global $wpdb, $apt_service;        $queries = $wpdb->get_results("SELECT * FROM $apt_service");        $count = 1;        foreach ($queries as $query) {            $query->service_id;            ?>                            <tr>                                <th  scope="col" ><?php echo $count; ?></th>            <?php if ($_GET['edit-service'] == $query->service_id) { ?>                            <form action="<?php echo $apturl . '&service=service'; ?>" method="post">                                <th  scope="col" ><input type="text" name="upname" id="upname" value='<?php echo $query->service_name; ?>' /></th>                                <th  scope="col" ><input type="text" name="upprice" id="upprice" value='<?php echo $query->service_price; ?>' /></th>                                <input type="hidden" name="srid" id="srid" value='<?php echo $query->service_id; ?>' />                                <th  scope="col" ><a id="add" href='<?php echo get_permalink() . "?page=createappoitment&dateslot=" . $query->service_id; ?>' ><img src="<?php echo $root; ?>/images/add.png" alt="Add Date Slot" title="Add Date Slot" height="18" width="18" /></a></th>                                <th  scope="col" ><a  href='<?php echo get_permalink() . "?page=createappoitment&timeslot=" . $query->service_id; ?>' ><img src="<?php echo $root; ?>/images/add.png" alt="Add Time Slot" title="Add Time Slot" height="18" width="18" /></a></th>                                <th  scope="col" ><input type="submit" name="update-service" id="update-service" value="" title="save service" /></th>                            </form>            <?php } else { ?>                            <th  scope="col" ><?php echo $query->service_name; ?></th>                            <th  scope="col" ><?php echo $query->service_price; ?></th>                            <th  scope="col" ><a id="add" href='<?php echo get_permalink() . "?page=createappoitment&dateslot=" . $query->service_id; ?>' ><img src="<?php echo $root; ?>/images/add.png" alt="Add Date Slot" height="18" width="18" title="Add Date Slot"/></a></th>                            <th  scope="col" ><a id="add" href='<?php echo get_permalink() . "?page=createappoitment&timeslot=" . $query->service_id; ?>' ><img src="<?php echo $root; ?>/images/add.png" alt="Add Time Slot" height="18" width="18" title="Add Time Slot"/></a></th>                            <th  scope="col" ><a id="edit" href="<?php echo $apturl . '&service&edit-service=' . $query->service_id; ?>"><img src="<?php echo $root; ?>/images/icon-edit.png" alt="edit" title="edit" height="16" width="16" />&nbsp;&nbsp;                                    <a href="<?php echo $apturl . '&service&delete-service=' . $query->service_id; ?>" ><img src="<?php echo $root; ?>/images/ico-close.png" alt="Delete" title="Delete" height="16" width="16" /></a></th>                        <?php } ?>                        </tr>                        <?php                        $count++;                    }                    ?>                    </tbody>                 </table>                <p class="service-text"><?php echo APT_TEXT; ?></p>            </div>    <?php } ?></div>    <!------ Time Slot condition ------->     <?php    if (isset($_GET['timeslot'])) {        $as = new AptService();        $timeid = $_GET['timeslot'];//update timeslot        if (isset($_POST['update-timeslot'])) {            global $wpdb, $apt_timeslot;            $wpdb->update($apt_timeslot, array('timeslot_start_time' => $_POST['smon'], 'timeslot_end_time' => $_POST['emon'], 'booking_number_time' => $_POST['editbooks']), array('timeslot_id' => $_POST['edit-timeid']), array('%s', '%s', '%d'), array('%d'));        }//all timeslot ontime add        if (isset($_POST['alldateadd'])) {            $int_time = $_POST['inttime'];            if (!empty($int_time)) {                $open_am_time = $_POST['opentime'] . '' . $_POST['selectopen'];                $close_am_time = $_POST['closetime'] . '' . $_POST['selectclose'];                $tsrand = $_POST['tsrand'];                $arr_day = array($_POST['chk_mon'], $_POST['chk_tue'], $_POST['chk_wed'], $_POST['chk_thu'], $_POST['chk_fri'], $_POST['chk_sat'], $_POST['chk_sun']);                $as->all_day_time_set($timeid, $open_am_time, $close_am_time, $int_time, $arr_day, $tsrand, $_POST['booktime']);            }        }        $aptser = $wpdb->get_row("SELECT * FROM $apt_service WHERE service_id= '$timeid'", ARRAY_N);// submit for sunday          if (!empty($timeid)) {            if (isset($_POST['addsun'])) {                $as = new AptService();                $start_sun = $_POST['strsun'] . ' ' . $_POST['strselect'];                $end_sun = $_POST['endsun'] . ' ' . $_POST['endselect'];                $as->insert_timeslot($_POST['hiddsun'], $start_sun, $end_sun, $_POST['tssun'], $_POST['tsrand'], $_POST['booksun']);            }// submit for monday              if (isset($_POST['addmon'])) {                $as = new AptService();                $start_mon = $_POST['strmon'] . ' ' . $_POST['strselectmon'];                $end_mon = $_POST['endmon'] . ' ' . $_POST['endselectmon'];                $as->insert_timeslot($_POST['hiddmon'], $start_mon, $end_mon, $_POST['tsmon'], $_POST['tsrand'], $_POST['bookmon']);            }// submit for Tuesday              if (isset($_POST['addtus'])) {                $as = new AptService();                $start_tus = $_POST['strtus'] . ' ' . $_POST['strselecttus'];                $end_tus = $_POST['endtus'] . ' ' . $_POST['endselecttus'];                $as->insert_timeslot($_POST['hiddtus'], $start_tus, $end_tus, $_POST['tstus'], $_POST['tsrand'], $_POST['booktue']);            }            // submit for Wednesday              if (isset($_POST['addwed'])) {                $as = new AptService();                $start_wed = $_POST['strwed'] . ' ' . $_POST['strselectwed'];                $end_wed = $_POST['endwed'] . ' ' . $_POST['endselectwed'];                $as->insert_timeslot($_POST['hiddwed'], $start_wed, $end_wed, $_POST['tswed'], $_POST['tsrand'], $_POST['bookwed']);            }            // submit for Thursday            if (isset($_POST['addthu'])) {                $as = new AptService();                $start_thu = $_POST['strthu'] . ' ' . $_POST['strselectthu'];                $end_thu = $_POST['endthu'] . ' ' . $_POST['endselectthu'];                $as->insert_timeslot($_POST['hiddthu'], $start_thu, $end_thu, $_POST['tsthu'], $_POST['tsrand'], $_POST['bookthu']);            }            // submit for Friday            if (isset($_POST['addfri'])) {                $as = new AptService();                $start_fri = $_POST['strfri'] . ' ' . $_POST['strselectfri'];                $end_fri = $_POST['endfri'] . ' ' . $_POST['endselectfri'];                $as->insert_timeslot($_POST['hiddfri'], $start_fri, $end_fri, $_POST['tsfri'], $_POST['tsrand'], $_POST['bookfri']);            }            // submit for Saturday	            if (isset($_POST['addsat'])) {                $as = new AptService();                $start_sat = $_POST['strsat'] . ' ' . $_POST['strselectsat'];                $end_sat = $_POST['endsat'] . ' ' . $_POST['endselectsat'];                $as->insert_timeslot($_POST['hiddsat'], $start_sat, $end_sat, $_POST['tssat'], $_POST['tsrand'], $_POST['booksat']);            }            ?>            <style> .aptservice{display:none;} </style>            <div class="time-slot-wrap">                <h1 class="timeslot-head"></h1>                <div class="tabel-scroll">                    <form action="<?php echo $apturl . '&timeslot=' . $timeid; ?>"  method="post"